changeSet.sql:
DROP TYPE IF EXISTS ticket_status;
CREATE TYPE ticket_status AS ENUM (
    'BOOKING_CREATED',
    'BOOKING_CANCELED',
    'BOOKING_EXPIRED',
    'ISSUED',
    'REFUND',
    'ERROR'
);

DROP TYPE IF EXISTS ticket_way;
CREATE TYPE ticket_way AS ENUM (
    'WAY_THERE',
    'WAY_BACK',
    'ROUND_TRIP',
    'COMPLEX_ROUTE'
);

DROP TYPE IF EXISTS flight_class;
CREATE TYPE flight_class AS ENUM (
    'ECONOMY',
    'PREMIUM_ECONOMY',
    'BUSINESS',
    'PREMIUM_BUSINESS',
    'FIRST',
    'PREMIUM_FIRST',
    'SUBSIDIZED'
);

DROP TYPE IF EXISTS flight_tariff;
CREATE TYPE flight_tariff AS ENUM (
    'BEST_LIGHT',
    'BEST_MEDIUM',
    'BEST_PRO',
    'BEST_SUPER_PRO'
);

DROP TYPE IF EXISTS order_status;
CREATE TYPE order_status AS ENUM (
    'NONE',
    'CANCELED',
    'IN_PROGRESS',
    'PAID',
    'COMPLETED'
);

DROP TYPE IF EXISTS provider;
CREATE TYPE provider AS ENUM (
    'MYAGENT_AVIA',
    'MYAGENT_HOTEL',
    'MYAGENT_RZD',
    'OSTROVOK_HOTEL',
    'IM_RZD',
    'VIPSERVICE_AVIA',
    'IWAY_TRANSFER'
);

DROP TYPE IF EXISTS product_status;
CREATE TYPE product_status AS ENUM (
    'NEW',
    'BOOKING_CREATED',
    'ISSUED',
    'BOOKING_CANCELED',
    'ERROR',
    'REFUND',
    'BOOKING_EXPIRED'
);
